import axios from 'axios';
import React, { useState } from 'react';
import { Button, Col, Container, Form, Row } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [logedInName, setLogedInName] = useState('');
  const [logedFailed, setLogedFailed] = useState('');
  const [flag, setFlag] = useState(null);
  const navigate = useNavigate();

  const changeHandler = (ev) => {
    const { name, value } = ev.target;
    if (name === 'username') setUsername(value);
    if (name === 'password') setPassword(value);
  };

  const submit = (e) => {
    e.preventDefault();
    axios.post('http://localhost:3001/login', { username, password })
      .then((response) => {
        if (response.data.logedIn) {
          setLogedInName(`${response.data.f_name.toUpperCase()} ${response.data.l_name.toUpperCase()}`);
          setFlag(1);
          setTimeout(() => {
            navigate('/', { state: { role: response.data.role } });
          }, 1500);
        } else {
          setLogedFailed(response.data.message);
          setFlag(0);
        }
      });
  };

  let loginStatus;
  if (flag === 1) {
    loginStatus = <div className="alert alert-success alert-dismissible fade show" role="alert">
      <strong>Welcome {logedInName}</strong>
    </div>;
  } else if (flag === 0) {
    loginStatus = <div className="alert alert-danger alert-dismissible fade show" role="alert">
      <strong>{logedFailed}</strong><br /> Please try again!
    </div>;
  }

  return (
    <Container className="my-5">
      <Row>
        <Col></Col>
        <Col>
          <Form>
            <Form.Group controlId="formBasicEmail">
              <Form.Label>User Name</Form.Label>
              <Form.Control onChange={changeHandler} name='username' type="text" placeholder="User name" required />
            </Form.Group>
            <Form.Group controlId="formBasicPassword">
              <Form.Label>Password</Form.Label>
              <Form.Control onChange={changeHandler} name='password' type="password" placeholder="Password" />
            </Form.Group>
            <Button onClick={submit} variant="primary" type="submit">
              Submit
            </Button>
          </Form>
        </Col>
        <Col>{loginStatus}</Col>
      </Row>
    </Container>
  );
};

export default Login;
