import React, { Component } from 'react';

class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            role: '' // Initially, role is empty
        };
    }

    componentDidMount() {
        // Get the role from the location state
        const role = this.props.location.state ? this.props.location.state.role : '';
        this.setState({ role });
    }

    render() {
        const { role } = this.state;
        return (
            <>
                {role === 'admin' ? <h1>Hello Admin</h1> : <h1>Hello User</h1>}
            </>
        );
    }
}

export default Home;
